import"@hotwired/turbo-rails";document.addEventListener("turbo:load",()=>{document.querySelectorAll("input.number-with-comma").forEach(e=>{e.value&&!e.value.includes(",")&&(e.value=Number(e.value).toLocaleString()),e.addEventListener("focus",()=>{e.value=e.value.replace(/,/g,"")}),e.addEventListener("blur",()=>{let t=e.value.replace(/,/g,"");t&&(e.value=Number(t).toLocaleString())})})}),document.addEventListener("submit",e=>{e.target.querySelectorAll("input.number-with-comma").forEach(e=>{e.value=e.value.replace(/,/g,"")})});